require('dotenv').config();
const Queue = require('bull');
const nodemailer = require("nodemailer");

// Define environment variables
const { REDIS_HOST, REDIS_PORT, EMAIL_HOST, EMAIL_PORT, EMAIL_USER, EMAIL_PASS } = process.env;

// Redis connection options for Bull queue
const redisOptions = {
    redis: {
        host: REDIS_HOST,
        port: REDIS_PORT
    }
};

// Create a Bull queue named 'send email'
const sendEmailQueue = new Queue('send email', redisOptions);

// Define the Nodemailer transporter for sending emails
const transporter = nodemailer.createTransport({
    host: EMAIL_HOST,
    port: EMAIL_PORT,
    secure: false, // true for port 465, false for other ports
    auth: {
        user: EMAIL_USER, // SMTP username
        pass: EMAIL_PASS  // SMTP password
    }
});

// Function to send email using the Nodemailer transporter
const sendMailOptions = async (emailData) => {
    try {
        const info = await transporter.sendMail({
            from: `"Maddison Foo Koch 👻" <${EMAIL_USER}>`, // Sender address
            to: emailData.to, // List of receivers from job data
            subject: emailData.subject, // Subject line from job data
            text: emailData.text, // Plain text body from job data
            html: emailData.html // HTML body from job data
        });

        console.log(`Message sent: ${info.from} to ${emailData.to}`);
    } catch (error) {
        console.error('Error sending email:', error);
        throw new Error('Failed to send email'); // Re-throw error to be handled by Bull queue
    }
};

function emailProcess() {
    // Process jobs from the 'send email' queue
    sendEmailQueue.process(async (job, done) => {
        try {
            console.log(`Processing job ID: ${job.id}`);
            await sendMailOptions(job.data); // Pass job data to sendMailOptions
            done(); // Mark job as done
        } catch (error) {
            done(error); // Mark job as failed and pass the error
        }
    });
}


sendEmailQueue.on('completed', (job) => {
    job.finished();
})

console.log("Queue and email job setup complete.");

module.exports = { emailProcess }

